import {Formik, Field,  FormikHelpers} from 'formik'
import {Row, Col, Form, FormSubtitle } from '../../styles/styles'
import { useAppDispatch, useAppSelector} from '../../app/hooks'
import { setJob }  from '../../features/job/job-slice'

interface Values {
    companyName: string;
    jobTitle: string;
    description: string;
    status: string;
}

const JobForm = () =>{
  const dispatch = useAppDispatch()

  return (
    <div>
      <Formik
        initialValues={{
          companyName: '',
          jobTitle: '',
          description: '',
          status: '',
        }}
        onSubmit={(values:Values, {setSubmitting}: FormikHelpers<Values>) => {
            dispatch(setJob(values))
            setSubmitting(false)
        }}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          touched,
          errors,
          values,
          isValid,
          dirty
        }) => (
          <Form onSubmit = {handleSubmit}>

                <FormSubtitle>
                    Job info
                </FormSubtitle>


            <Row>
                <Col>
                <Field id="companyName" name="companyName" placeholder = "Company" />
                </Col>
                <Col>

                    <Field id="jobTitle" name="jobTitle" placeholder = "Job Title" />
                </Col>
            </Row>
            <Row>
                <Col>

                    <Field as= "textarea" id="description" name="description" placeholder = "Job description..." />
                </Col>
            </Row>
            <FormSubtitle>
                Activities
            </FormSubtitle>
            <Row>
            <Col>


            </Col>
            </Row>





          <button type="submit">Save</button>
        </Form>
        )}

      </Formik>
    </div>
)}
export default JobForm
